/**
 * SVG icons helper functions
 * 
 * @package TienNguyen
 * @version 1.0.0
 */

/**
 * Default SVG icons for theme.
 */
if (!class_exists('SVG_icons')) {

    class SVG_icons
    {
        /**
         * Output and Get Theme SVG.
         * 
         * @param string $icon The name of the icon.
         * @param string $color The color code.
         * @param string|array $class Add more class for svg.
         */
        public static function get_svg($icon, $color, $class)
        {
            $arr_icons = self::$icons;

            if (is_array($class)) {
                $class = implode(' ', $class);
            }

            if (array_key_exists($icon, $arr_icons)) {
                $rep_string = '<svg class="' . $class . '" aria-hidden="true" role="img" focusable="false" ';
                $svg = preg_replace('/^<svg /', $rep_string, trim($arr_icons[$icon])); // Add extra atributres to SVG code.
                $svg = str_replace('#1A1A1B', $color, $svg); // Replace the color.
                // $svg = str_replace('#', '%23', $svg); // UrlEncode hashed.
                $svg = preg_replace("/([\t\n]+)/", ' ', $svg); // Remove newlines & tabs.
                $svg = preg_replace('/>\s*</', '><', $svg); // Remove whitespace between SVG tags.
                return $svg;
            }
            return null;
        }

