/**
 * Output and get the SVG icon.
 * 
 * @param $svg_name The name of the icon.
 * @param $color Color code.
 */
if (!function_exists('the_theme_svg')) {
    function the_theme_svg($svg_name, $color = '', $class = 'svg-icon')
    {
        echo get_theme_svg($svg_name, $color, $class);
    }
}

/**
 * Get the SVG icon.
 * 
 * @param $svg_name The name of the icon.
 * @param $color Color code.
 */
if (!function_exists('get_theme_svg')) {
    function get_theme_svg($svg_name, $color = '', $class = 'svg-icon')
    {
        // Make sure that only our allowed tags and attributes are included.
        $svg = wp_kses(
            SVG_icons::get_svg($svg_name, $color, $class),
            [
                'ellipse'   => [
                    'cx'    => true,
                    'cy'    => true,
                    'rx'    => true,
                    'ry'    => true,
                    'style' => true,
                ],
                'polyline' => [
                    'class'  => true,
                    'points' => true,
                ],
                'text' => [
                    'class' => true,
                    'id'    => true,
                ],
                'g' => [
                    'class' => true,
                    'id'    => true,
                ],
                'rect' => [
                    'class'  => true,
                    'id'     => true,
                    'x'      => true,
                    'y'      => true,
                    'style'  => true,
                    'height' => true,
                    'width'  => true
                ],
                'circle' => [
                    'class' => true,
                ],
                'title' => [],
                'defs'  => [],
                'style' => [],
                'svg'   => [
                    'class'       => true,
                    'xmlns'       => true,
                    'width'       => true,
                    'height'      => true,
                    'viewbox'     => true,
                    'aria-hidden' => true,
                    'role'        => true,
                    'focusable'   => true,
                    'id'          => true,
                ],
                'path'    => [
                    'fill'      => true,
                    'fill-rule' => true,
                    'd'         => true,
                    'transform' => true,
                    'style'     => true
                ],
                'polygon' => [
                    'fill'      => true,
                    'fill-rule' => true,
                    'points'    => true,
                    'transform' => true,
                    'focusable' => true,
                    'style' => true,
                ],
                'clipPath' => [
                    'id'        => true,
                    'transform' => true
                ]
            ]
        );

        if (!$svg) {
            return false;
        }

        return $svg;
    }
}
